if snd.class == "Ascendril" then snd.starting_attack()
function snd.attack_function() if not snd.waiting.queue then local string = "" -- we'll load this with our attack string local call = "" -- and this with our affliction call local limbs = {"left leg", "right leg", "left arm", "right arm"} local targ = "nothing" local stalled = false if snd.defenses.def_stalled.state == "deffed" then stalled = true end for limb in pairs(limbs) do -- ignore last parried, last hit, and any limbs being resto'd if limbs[limb] ~= snd.parried_limb and limbs[limb] ~= snd.last_hit_limb and limbs[limb] ~= snd.resto_limb then if targ == "nothing" then targ = limbs[limb] end end end if snd.glimpse == "none" and not snd.glimpsing and not snd.checkAff("frostbrand") then string = "fulcrum glimpse water" elseif snd.glimpse == "none" and snd.checkAff("frostbrand") then string = "fulcrum catalyst " .. snd.target .. " frost" elseif snd.used.shield == true then string = "cast windlance " .. snd.target elseif snd.glimpse == "water" or snd.glimpse == "fire" or snd.glimpse == "air" then if gmcp.Char.Vitals.ability_bal == "1" then string = "fulcrum flare " .. snd.target end if gmcp.Char.Vitals.resonance == "water" then if not stalled then if snd.limb_dmg.torso == 0 and snd.parried_limb ~= "torso" then string = string .. snd.sep .. "fulcrum stall 3 firelash " .. snd.target .. " torso" else string = string .. snd.sep .. "fulcrum stall 3 firelash " .. snd.target .. " " .. targ end elseif stalled then if snd.checkAff("no_caloric") and not snd.checkAff("direfrost") and not snd.checkAff("frostbrand") then string = string .. snd.sep .. "cast direfrost " .. snd.target elseif snd.checkAff("frozen") and not snd.checkAff("writhe_impaled") then string = string .. snd.sep .. "cast icicle " .. snd.target else string = string .. snd.sep .. "cast firelash " .. snd.target .. " " .. targ end end elseif gmcp.Char.Vitals.resonance == "fire" then if not stalled then if snd.limb_dmg.torso == 0 and snd.parried_limb ~= "torso" then string = string .. snd.sep .. "fulcrum stall 3 firelash " .. snd.target .. " torso" else string = string .. snd.sep .. "fulcrum stall 3 firelash " .. snd.target .. " " .. targ end elseif stalled then if snd.checkAff("no_caloric") and not snd.checkAff("direfrost") and not snd.checkAff("frostbrand") then string = string .. snd.sep .. "cast direfrost " .. snd.target elseif snd.checkAff("frozen") and not snd.checkAff("writhe_impaled") then string = string .. snd.sep .. "cast icicle " .. snd.target else string = string .. snd.sep .. "cast firelash " .. snd.target .. " " .. targ end end elseif gmcp.Char.Vitals.resonance == "air" then if not stalled then if snd.limb_dmg.torso == 0 and snd.parried_limb ~= "torso" then string = string .. snd.sep .. "fulcrum stall 3 firelash " .. snd.target .. " torso" else string = string .. snd.sep .. "fulcrum stall 3 firelash " .. snd.target .. " " .. targ end elseif stalled then --not snd.direfrost and not snd.checkAff("frostbrand") then if snd.checkAff("no_caloric") and not snd.checkAff("direfrost") and not snd.checkAff("frostbrand") then string = string .. snd.sep .. "cast direfrost " .. snd.target elseif snd.checkAff("frozen") and not snd.checkAff("writhe_impaled") then string = string .. snd.sep .. "cast icicle " .. snd.target else string = string .. snd.sep .. "cast firelash " .. snd.target .. " " .. targ end end end end if string ~= snd.last_attack and not snd.waiting.queue then snd.last_attack = string snd.attack(snd.last_attack) snd.waiting.queue = true tempTimer(snd.delay(), [[snd.waiting.queue = false]]) end end end
snd.attack_function() end

if snd.class == "Runecarver" then snd.starting_attack() function snd.attack_function()
local string = "" if not snd.waiting.queue then limbs = {"left leg", "right leg", "left arm", "right arm"} for limb in pairs(limbs) do -- ignore last hit and any limbs being resto'd if limbs[limb] ~= snd.last_hit_limb and limbs[limb] ~= snd.resto_limb then if targ == nil then targ = limbs[limb] end end end
if snd.used.shield then string = string.."hex imperil "..snd.target elseif gmcp.Char.Vitals.morass == "" then if hasSkill("Colonisation") then string = string.."spore colonise clutchgill" else string = string.."spore germinate" end elseif snd.gravity < 5 then string = string.."spore ensnare "..snd.target else if not snd.checkAff(targ:gsub(" ","_").."_crippled") then string = string.."spore degrade "..snd.target.." "..targ else if hasSkill("Bewitch") and snd.balance.sorcery_transfix then string = string.."runecarve bewitch "..snd.target else string = string.."hex numb "..snd.target end end end if string ~= snd.last_attack and not snd.waiting.queue then snd.last_attack = string snd.attack(snd.last_attack) snd.waiting.queue = true tempTimer(snd.delay(), [[snd.waiting.queue = false]]) end end end snd.attack_function() end

if snd.class == "Templar" then snd.starting_attack() function snd.attack_function() if not snd.waiting.queue then local string = "" -- we'll load this with our attack string local call = "" -- and this with our affliction call -- next, the afflictions we want to give local touse = {} local tohit = {} -- and in case we don't find enough of them local extraaff = "hemorrhage" if tonumber(gmcp.Char.Vitals.charge_left) >= 100 and tonumber(gmcp.Char.Vitals.charge_right) >= 100 then iceblast = true end -- set the affs we want to use snd.giving = { "left_arm_crippled", "right_arm_crippled", "left_leg_crippled", "right_leg_crippled", "stupidity", "anorexia", "slickness", "asthma", "paresis", "clumsiness", "weariness", }
-- pick affs from the list above for i in pairs(snd.giving) do -- make sure not to double up paresis and paralysis if not snd.checkAff(snd.giving[i]) then -- and don't add an aff we've already got in the list! if not table.contains(touse, snd.giving[i]) then table.insert(touse, snd.giving[i]) end end end
-- first and foremost, check if we can retri! if snd.target_impaled then string = "disembowel "..snd.target elseif snd.proned() and not snd.checkAff("writhe_impaled") then string = "penance "..snd.target..snd.sep.."impale "..snd.target else -- now let's convert affs to venoms/empowerments for aff in pairs(touse) do table.insert(tohit, snd.effects[touse[aff]]) end -- if we didn't find enough stuff to hit with, stick in our filler while #tohit < 3 do table.insert(tohit, extraaff) end -- now to decide what attack to use! if snd.used.shield and snd.used.rebounding then string = "razestrike "..snd.target.." blaze" elseif snd.used.rebounding or snd.used.shield then string = "razestrike "..snd.target.." "..tohit[1] call = "wt Afflicting "..snd.target..": "..tohit[1]..snd.sep elseif iceblast then string = "blade release right iceblast "..snd.target..snd.sep.."blade release left iceblast "..snd.target..snd.sep.."dsk "..snd.target.." "..tohit[2].." "..tohit[1] call = "wt Afflicting "..snd.target..": "..tohit[2]..", "..tohit[1]..snd.sep else string = "dsk "..snd.target.." "..tohit[2].." "..tohit[1] call = "wt Afflicting "..snd.target..": "..tohit[2]..", "..tohit[1]..snd.sep end end -- prepend our affliction call, if we need one if snd.toggles.affcalling and not snd.target_gone then string = call..string end -- handle anti-spam and the actual attacking now if string ~= snd.last_attack and not snd.waiting.queue then snd.last_attack = string snd.attack(snd.last_attack) snd.waiting.queue = true tempTimer(snd.delay(), [[snd.waiting.queue = false]]) end end end snd.attack_function() end

if snd.class == "Zealot" then snd.starting_attack()
function snd.attack_function() -- This checks for a present Firefist defense. This is used later during a Firefist check. local firefist = false if snd.defenses.def_firefist.state == "deffed" then firefist = true end -- This checks for a present Zenith defense. It is used later in Zenith/Flow Handling. local zenith = false if snd.defenses.def_zenith.state == "deffed" then zenith = true end -- This is a failsafe to take crispy off of a target that has cured ablaze. if snd.checkAff("crispy") then if not snd.checkaff("ablaze") then snd.target_cured("crispy") end end -- This uses crispy to check if they're at the right flame level. local immolation_ready = false if snd.checkAff("ablaze") then if snd.checkAff("crispy") then immolation_ready = true end end -- In this mode, we're going to only rely on Purification. It's built to support a quick Immolate. if not snd.waiting.queue then if snd.used.shield then string = "touch hammer " .. snd.target -- OK, so if immolation is ready... we should cast it! elseif immolation_ready then string = "enact immolation " .. snd.target -- OK, so if immolation isn't ready, what next? Heatspear! elseif not snd.checkAff("heatspear") and snd.checkAff("ablaze") then string = "enact heatspear " .. snd.target -- OK, what if they have heatspear? Are they ablaze?! Quicken! -- elseif snd.checkAff("ablaze") then -- string = "enact quicken "..snd.target -- OK, so we've done all of this, but they don't have ablaze! Ez fix.. else string = "enact scorch " .. snd.target end -- Flow Handling/Zenith Handling -- In this mode, we only Flow when we have Zenith, otherwise we are casting. -- This means our Flow handling IS our Zenith handling. -- Our Flow attacks should always be punches to take advantage of Firefist. if zenith then if snd.used.shield then string = "touch hammer " .. snd.target elseif snd.parried_limb ~= "torso" then string = "flow " .. snd.target .. " clawtwist clawtwist" .. snd.sep .. string elseif snd.parried_limb ~= "left arm" then string = "flow " .. snd.target .. " pummel left pummel left" .. snd.sep .. string else string = "flow " .. snd.target .. " pummel right pummel right" .. snd.sep .. string end end -- Hackles handling -- In this mode, we really just hackles whipburst. -- We will Whipburst even in lieu of a Flow combo. -- First, we should check if we have hackles balance. if gmcp.Char.Vitals.ability_bal == "1" then string = "hackles " .. snd.target .. " whipburst " .. snd.sep .. string end end -- We want to turn on firefist if we have Zenith up, because that's the only time we're punching and it needs to count. if not firefist then if snd.checksomeAffs({"ablaze", "heatspear"}, 2) then if zenith then string = "enact firefist" else string = "enact zenith" .. snd.sep .. string end end end -- I am leaving this failsafe in... below checks if we're g2g with the immolate button. if immolation_ready then string = "enact immolation " .. snd.target end if string ~= snd.last_attack and not snd.waiting.queue then snd.last_attack = string snd.attack(snd.last_attack) snd.waiting.queue = true tempTimer(snd.delay(), [[snd.waiting.queue = false]]) end end
snd.attack_function() end

if snd.class == "Zealot" then snd.starting_attack()
function snd.attack_function() local firefist = false if snd.defenses.def_firefist.state == "deffed" then firefist = true end local zenith = false if snd.defenses.def_zenith.state == "deffed" then zenith = true end if snd.checkAff("crispy") then if not snd.checkaff("ablaze") then snd.target_cured("crispy") end end local immolation_ready = false if snd.checkAff("ablaze") then if snd.checkAff("crispy") then immolation_ready = true end end -- Flow Handling -- Consider writing a check for different combos on firefist? if not snd.waiting.queue then if snd.used.shield then string = "touch hammer " .. snd.target elseif snd.parried_limb ~= "torso" then string = "flow " .. snd.target .. " clawtwist clawtwist" elseif snd.parried_limb ~= "head" then if not snd.checkAff("crippled_throat") then string = "flow " .. snd.target .. " edgekick heelrush head" else string = "flow " .. snd.target .. " sunkick heelrush head" end elseif snd.parried_limb ~= "left leg" and snd.parried_limb ~= "right leg" then string = "flow " .. snd.target .. " wanekick left wanekick right" elseif snd.parried_limb ~= "left leg" and snd.parried_limb == "right leg" then string = "flow " .. snd.target .. " wanekick left wanekick left" elseif snd.parried_limb ~= "right leg" and snd.parried_limb == "left leg" then string = "flow " .. snd.target .. " wanekick right wanekick right" else if snd.parried_limb ~= "left arm" then string = "flow " .. snd.target .. " pummel left pummel left" end end -- Hackles handling -- Jawcrack > Rive ... rive to maximize the bleeding. -- Prioritize Whipburst during firefist? -- First, we should check if we have hackles balance. if gmcp.Char.Vitals.ability_bal == "1" then -- Cool! What are we doing with it? Let's figure that out and put it before our flow string! if snd.parried_limb ~= "head" and not snd.checkAff("stuttering") then string = "hackles " .. snd.target .. " jawcrack " .. snd.sep .. string else string = "hackles " .. snd.target .. " rive " .. snd.sep .. string end end -- Zenith handling -- Immolation > Pyromania > Infernal > Heatspear > Quicken > Scorch -- handle pyromania with a variable? -- use the limb state checker from templar limbs script for infernal check? -- handle heatspear as an aff.. -- check if immolate not possible ... if not, quicken or scorch if zenith then if not snd.checkAff("heatspear") and snd.checkAff("ablaze") then string = string .. snd.sep .. "enact heatspear " .. snd.target elseif snd.checkAff("ablaze") then string = string .. snd.sep .. "enact quicken " .. snd.target else string = string .. snd.sep .. "enact scorch " .. snd.target end end if not firefist then if snd.checksomeAffs({"ablaze", "heatspear"}, 2) then string = "enact firefist" end end if immolation_ready then string = "enact immolation " .. snd.target end if string ~= snd.last_attack and not snd.waiting.queue then snd.last_attack = string snd.attack(snd.last_attack) snd.waiting.queue = true tempTimer(snd.delay(), [[snd.waiting.queue = false]]) end end end
snd.attack_function() end

