if snd.used.rebounding and snd.used.shield then string = "raze "..snd.target elseif snd.used.rebounding or snd.used.shield then string = "pole razehack "..snd.target.." "..right if snd.toggles.affcalling and not snd.target_gone then x = "wt Afflicting "..snd.target..": "..right..snd.sep end else if snd.checkAff("fallen") then if table.contains(snd.target_has, "crushed_chest") then string = "hammer pulverize "..snd.target else string = "hammer crush "..snd.target.." chest" end else string = "pole spinslash "..snd.target.." "..left.." "..right if snd.toggles.affcalling and not snd.target_gone then x = "wt Afflicting "..snd.target..": "..left..", "..right..snd.sep end end end
