if snd.class == "Revenant" then snd.starting_attack() function snd.attack_function() if not snd.waiting.queue then local string = "" -- we'll load this with our attack string local call = "" -- and this with our affliction call -- next, the afflictions we want to give local touse = {} local tohit = {} -- and in case we don't find enough of them local extraaff = "hemorrhage" if tonumber(gmcp.Char.Vitals.charge_left) >= 100 and tonumber(gmcp.Char.Vitals.charge_right) >= 100 then telvi = true end -- set the affs we want to use snd.giving = { "left_arm_crippled", "right_arm_crippled", "left_leg_crippled", "right_leg_crippled", "stupidity", "anorexia", "slickness", "asthma", "paresis", "clumsiness", "weariness", }
