-- first and foremost, check if we can retri! if snd.target_impaled then string = "extirpate "..snd.target elseif snd.proned() and not snd.checkAff("writhe_impaled") then string = "lurk "..snd.target..snd.sep.."transpierce "..snd.target else -- now let's convert affs to venoms/empowerments for aff in pairs(touse) do table.insert(tohit, snd.effects[touse[aff]]) end -- if we didn't find enough stuff to hit with, stick in our filler while #tohit < 3 do table.insert(tohit, extraaff) end -- now to decide what attack to use! if snd.used.shield and snd.used.rebounding then string = "deceive "..snd.target.." wasi" elseif snd.used.shield or snd.used.rebounding then string = "deceive "..snd.target..snd.rev_empower(tohit[1]) call = "wt Afflicting "..snd.target..": "..tohit[1]..snd.sep elseif telvi then string = "blade scribe right telvi "..snd.target..snd.sep.."blade scribe left telvi "..snd.target..snd.sep.."dpl "..snd.target.." "..snd.rev_empower(tohit[2]).." "..snd.rev_empower(tohit[1]) call = "wt Afflicting "..snd.target..": "..tohit[2]..", "..tohit[1]..snd.sep else string = "dpl "..snd.target.." "..snd.rev_empower(tohit[2]).." "..snd.rev_empower(tohit[1]) call = "wt Afflicting "..snd.target..": "..tohit[2]..", "..tohit[1]..snd.sep end end -- prepend our affliction call, if we need one if snd.toggles.affcalling and not snd.target_gone then string = call..string end -- handle anti-spam and the actual attacking now if string ~= snd.last_attack and not snd.waiting.queue then snd.last_attack = string snd.attack(snd.last_attack) snd.waiting.queue = true tempTimer(snd.delay(), [[snd.waiting.queue = false]]) end end end snd.attack_function() end

