function snd.attack_function() if not snd.waiting.queue then local string = "" -- we'll load this with our attack string local call = "" -- and this with our affliction call local limbs = {"left leg", "right leg", "left arm", "right arm"} local targ = "nothing" local stalled = false if snd.defenses.def_arrhythmia.state == "deffed" then arrhythmia = true end for limb in pairs(limbs) do -- ignore last parried, last hit, and any limbs being resto'd if limbs[limb] ~= snd.parried_limb and limbs[limb] ~= snd.last_hit_limb and limbs[limb] ~= snd.resto_limb then if targ == "nothing" then targ = limbs[limb] end end end if snd.glimpse == "none" and not snd.glimpsing and not snd.checkAff("frostbite") then string = "well imbrue" elseif snd.glimpse == "none" and snd.checkAff("frostbite") then string = "well actualize " .. snd.target .. " phlegm" elseif snd.used.shield == true then string = "humour perforate " .. snd.target elseif snd.glimpse == "phlegm" or snd.glimpse == "yellow" or snd.glimpse == "black" then if gmcp.Char.Vitals.ability_bal == "1" then string = "well palpitate " .. snd.target end if gmcp.Char.Vitals.aspect == "phlegm" then if not stalled then if snd.limb_dmg.torso == 0 and snd.parried_limb ~= "torso" then string = string .. snd.sep .. "well arrhythmia 3 disgorge " .. snd.target .. " torso" else string = string .. snd.sep .. "well arrhythmia 3 disgorge " .. snd.target .. " " .. targ end elseif stalled then if snd.checkAff("no_caloric") and not snd.checkAff("frostbite") and not snd.checkAff("excess_phlegm") then string = string .. snd.sep .. "humour frostbite " .. snd.target elseif snd.checkAff("frozen") and not snd.checkAff("writhe_impaled") then string = string .. snd.sep .. "humour perforate " .. snd.target else string = string .. snd.sep .. "humour disgorge " .. snd.target .. " " .. targ end end elseif gmcp.Char.Vitals.aspect == "yellow" then if not stalled then if snd.limb_dmg.torso == 0 and snd.parried_limb ~= "torso" then string = string .. snd.sep .. "well arrhythmia 3 disgorge " .. snd.target .. " torso" else string = string .. snd.sep .. "well arrhythmia 3 disgorge " .. snd.target .. " " .. targ end elseif stalled then if snd.checkAff("no_caloric") and not snd.checkAff("frostbite") and not snd.checkAff("excess_phlegm") then string = string .. snd.sep .. "humour frostbite " .. snd.target elseif snd.checkAff("frozen") and not snd.checkAff("writhe_impaled") then string = string .. snd.sep .. "humour perforate " .. snd.target else string = string .. snd.sep .. "humour disgorge " .. snd.target .. " " .. targ end end elseif gmcp.Char.Vitals.aspect == "black" then if not stalled then if snd.limb_dmg.torso == 0 and snd.parried_limb ~= "torso" then string = string .. snd.sep .. "well arrhythmia 3 disgorge " .. snd.target .. " torso" else string = string .. snd.sep .. "well arrhythmia 3 disgorge " .. snd.target .. " " .. targ end elseif stalled then --not snd.direfrost and not snd.checkAff("frostbrand") then if snd.checkAff("no_caloric") and not snd.checkAff("frostbite") and not snd.checkAff("excess_phlegm") then string = string .. snd.sep .. "humour frostbite " .. snd.target elseif snd.checkAff("frozen") and not snd.checkAff("writhe_impaled") then string = string .. snd.sep .. "humour perforate " .. snd.target else string = string .. snd.sep .. "humour disgorge " .. snd.target .. " " .. targ end end end end if string ~= snd.last_attack and not snd.waiting.queue then snd.last_attack = string snd.attack(snd.last_attack) snd.waiting.queue = true tempTimer(snd.delay(), [[snd.waiting.queue = false]]) end end end
