snd.class_list = { {class = "Akkari", func = function(current) snd.akkari_bash() end}, {class = "Alchemist", func = function(current) snd.alchemist_bash() end}, {class = "Archivist", func = function(current) snd.archivist_bash() end}, {class = "Ascendril", func = function(current) snd.ascendril_bash() end}, {class = "Bard", func = function(current) snd.bard_bash() end}, {class = "Bloodborn", func = function(current) snd.bloodborn_bash() end}, {class = "Carnifex", func = function(current) snd.carnifex_bash() end}, {class = "Earthcaller", func = function(current) snd.earthcaller_bash() end}, {class = "Indorani", func = function(current) snd.indorani_bash() end}, {class = "Luminary", func = function(current) snd.luminary_bash() end}, {class = "Monk", func = function(current) snd.monk_bash() end}, {class = "Oneiromancer", func = function(current) snd.oneiromancer_bash() end}, {class = "Praenomen", func = function(current) snd.praenomen_bash() end}, {class = "Ravager", func = function(current) snd.ravager_bash() end}, {class = "Revenant", func = function(current) snd.revenant_bash() end}, {class = "Runecarver", func = function(current) snd.runecarver_bash() end}, {class = "Sciomancer", func = function(current) snd.sciomancer_bash() end}, {class = "Sentinel", func = function(current) snd.sentinel_bash() end}, {class = "Shaman", func = function(current) snd.shaman_bash() end}, {class = "Shapeshifter", func = function(current) snd.shapeshifter_bash() end}, {class = "Syssin", func = function(current) snd.syssin_bash() end}, {class = "Templar", func = function(current) snd.templar_bash() end}, {class = "Teradrim", func = function(current) snd.teradrim_bash() end}, {class = "Tidesage", func = function(current) snd.tidesage_bash() end}, {class = "Warden", func = function(current) snd.warden_bash() end}, {class = "Wayfarer", func = function(current) snd.wayfarer_bash() end}, {class = "Zealot", func = function(current) snd.zealot_bash() end}, {class = "None", func = function(current) snd.none_bash() end}, --200 }
function snd.shield_check() if snd.defenses.def_shield_tattoo.state ~= "deffed" then return true end end
function snd.bashing_function() if not snd.room_clear then return end
if not snd.waiting.queue then if not snd.bashing.targeted then snd.runBasher() else battack = "none" -- set attack to none if not snd.counterattack_active then -- if the npc isn't counter attacking (tcanna/drakkum/torturer), then check class skills for k,v in pairs(snd.class_list) do if snd.class_list[k].class == snd.class then -- check current class to class bash list above battack = "" -- set attack from 'none' to "" so it can be set in the class basher, will provide error if it can't get a class ability if snd[v.class:lower().."_bash_override"] then snd[v.class:lower().."_bash_override"]() else v.func() -- set attack from class basher end if snd.have_aff("shock") and hasSkill("Overdrive") then -- let's use overdrive if we have shock, adds to new class bashing attack line battack = "overdrive"..snd.sep..battack..snd.sep.."recoup"..snd.sep elseif snd.pushattack then -- if they are a pushable npc (tcanna/drakuum/torturer) and we know we can shove, do it first battack = "push "..snd.bashing.target..snd.sep.."recoup"..snd.sep snd.pushattack = false end end end else -- if they're counter attacking, let's not do anything battack = "" end
-- if snd.probed then -- If a npc is probed, move along -- else -- else, in three areas that have advanced bashing, probe the npc to see if they are a punisher -- if gmcp.Room.Info.area == "Tcanna Island" or gmcp.Room.Info.area == "Drakuum" or gmcp.Room.Info.area == "the Torturers' Caverns" then -- battack = "probe "..snd.bashing.target..snd.sep..battack..snd.sep -- we're probing here and then attacking from above -- end -- note, this is just to make sure you don't get owned by shielding on punisher type npcs -- end -- if tonumber(gmcp.Char.Vitals.bleeding) > 500 and hasSkill("Clotting") then -- if you are bleeding a lot and can clot, clot with your attack -- mp = 100*(gmcp.Char.Vitals.mp/gmcp.Char.Vitals.maxmp) -- if mp >= 50 then -- battack = "clot 500"..snd.sep..battack..snd.sep -- end -- end
if tonumber(snd.toggles.gauntlet_level) >= 2 then battack = battack..snd.sep.."absorb ylem" end -- if you have a level 2 gauntlet, auto-absorb
if battack == "" and (snd.counterattack_active or not snd.defenses.def_reflection.state == "deffed") then -- if the basher couldn't change battack to something from your class, it will give an error echo("\nTried to bash, no class ability set.") elseif battack ~= snd.last_attack and not snd.ylem_check then -- if your battack isn't the same as your last attempted attack, and you aren't trying to capture ylem snd.last_attack = battack -- ATTACK if battack ~= "none" then -- if for some reason it's none as your attack, stand snd.send("qeb stand"..snd.sep..snd.last_attack) end snd.waiting.queue = true tempTimer(snd.delay(), [[snd.waiting.queue = false]]) end
end end end
function snd.akkari_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) wp = 100*(gmcp.Char.Vitals.wp/gmcp.Char.Vitals.maxwp) bp = tonumber(gmcp.Char.Vitals.blood)
if hp <=40 and snd.shield_check() then if snd.punisher then -- if they will kill you for having a shield, don't do it else battack = "touch shield" -- otherwise, shield end elseif hp <= 45 and bp >= 30 and snd.balance.mend and hasSkill("Vivify") then battack = "vivify" elseif bp >= 50 and hp <= 30 and hasSkill("Unbroken") then if snd.balance.unbroken and snd.defenses.def_unbroken.state == "down" then battack = "spirit unbroken"..snd.sep.."denounce "..snd.bashing.target else battack = "denounce "..snd.bashing.target end elseif bp <= 25 then battack = "deliver corpse" else battack = "denounce "..snd.bashing.target end end
function snd.alchemist_bash()
hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) mp = 100*(gmcp.Char.Vitals.mp/gmcp.Char.Vitals.maxmp) wp = 100*(gmcp.Char.Vitals.wp/gmcp.Char.Vitals.maxwp)
if snd.familiar and snd.balance.morph and hasSkill("Reconfiguration") then if snd.shamanpet == "tumefactive" then battack = "order experiment passive"..snd.sep.."experiment reconfigure toxic"..snd.sep elseif snd.shamanpet == "toxic" then battack = "order experiment passive"..snd.sep.."experiment reconfigure tumefactive"..snd.sep else battack = "order experiment passive"..snd.sep.."experiment reconfigure tumefactive"..snd.sep end else battack = "experiment recall"..snd.sep.."order experiment passive"..snd.sep.."order experiment follow me"..snd.sep end
if hp <=40 and snd.shield_check() then if snd.punisher then else battack = "touch shield" end else if wp>=45 and tonumber(gmcp.Char.Vitals.volatility) > 3 then battack = battack.."alchemy catalyse"..snd.sep.."alchemy static "..snd.bashing.target else battack = battack.."alchemy static "..snd.bashing.target end end
end
function snd.archivist_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) bioessence = tonumber(gmcp.Char.Vitals.bio) or 0 mp = 100*(gmcp.Char.Vitals.mp/gmcp.Char.Vitals.maxmp)
if hp <= 70 and not snd.knit and bioessence > 0 and hasSkill("Knitting") then battack = "bio knitting" elseif hp <= 60 and bioessence > 1 and not snd.balance.ethereal and hasSkill("Ethereal") then battack = "bio ethereal" elseif hp <=40 and snd.shield_check() then if snd.punisher then else battack = "touch shield" end else if bioessence > 1 and hasSkill("Stimulant") then battack = "incite crux "..snd.bashing.target..snd.sep.."bio stimulant" else battack = "incite crux " ..snd.bashing.target end end end
function snd.ascendril_bash()
hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) wp = 100*(gmcp.Char.Vitals.wp/gmcp.Char.Vitals.maxwp)
if hp <=40 and snd.shield_check() then if snd.punisher then else battack = "touch shield" end elseif snd.defenses.def_reflection.state == "deffed" and hp <= 60 then -- let the reflection do its job elseif hp <= 60 then if snd.reflection_charge > 0 then battack = "cast reflection" else if snd.shield_check() and not snd.punisher then battack = "touch shield" end end elseif hasSkill("Quickcast") then if not snd.fulcrum_exists then battack = "fulcrum construct" elseif snd.fulcrum_call then battack = "fulcrum recall" else if snd.toggles.enrich and snd.balance.enrich and wp >= 40 then if (gmcp.Char.Vitals.resonance == "fire" or gmcp.Char.Vitals.resonance == "air") then battack = "fulcrum enrich water"..snd.sep end end if snd.toggles.prism then if gmcp.Char.Vitals.resonance == "" or gmcp.Char.Vitals.resonance == "air" then battack = battack.."cast prism firelash "..snd.bashing.target elseif gmcp.Char.Vitals.resonance == "air" then battack = "cast prism frostblade "..snd.bashing.target elseif gmcp.Char.Vitals.resonance == "fire" then battack = "cast prism windlance "..snd.bashing.target end else if gmcp.Char.Vitals.resonance == "" or gmcp.Char.Vitals.resonance == "air" then battack = "cast frostblade "..snd.bashing.target elseif gmcp.Char.Vitals.resonance == "water" then battack = "cast firelash "..snd.bashing.target elseif gmcp.Char.Vitals.resonance == "fire" then battack = "cast windlance "..snd.bashing.target end end end else battack = "cast firelash "..snd.bashing.target end end
function snd.bard_bash()
hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp)
if hp <=40 and snd.shield_check() then if snd.punisher then else battack = "touch shield" end else if tonumber(gmcp.Char.Vitals.dithering) > 0 then battack = "tempo "..snd.bashing.target else battack = "weave tearing "..snd.bashing.target end end end
function snd.bloodborn_bash()
hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) wp = 100*(gmcp.Char.Vitals.wp/gmcp.Char.Vitals.maxwp)
if hp <=40 and snd.shield_check() then if snd.punisher then else battack = "touch shield" end elseif snd.defenses.def_reflection.state == "deffed" and hp <= 60 then -- let the reflection do its job elseif hp <= 60 and snd.reflection_charge > 0 then battack = "unleash geminate" elseif hasSkill("Primacy") then if not snd.fulcrum_exists then battack = "well draw" elseif snd.fulcrum_call then battack = "well recall" else if snd.toggles.enrich and snd.balance.enrich and wp >= 40 then if (gmcp.Char.Vitals.aspect == "yellow" or gmcp.Char.Vitals.aspect == "black") then battack = "well contaminate phlegm"..snd.sep end end if snd.toggles.prism then if gmcp.Char.Vitals.aspect == "" or gmcp.Char.Vitals.aspect == "black" then battack = battack.."unleash triplicate disgorge "..snd.bashing.target elseif gmcp.Char.Vitals.aspect == "black" then battack = "unleash triplicate flense "..snd.bashing.target elseif gmcp.Char.Vitals.aspect == "yellow" then battack = "unleash triplicate befoul "..snd.bashing.target end else if gmcp.Char.Vitals.aspect == "" or gmcp.Char.Vitals.aspect == "black" then battack = "humour flense "..snd.bashing.target elseif gmcp.Char.Vitals.aspect == "phlegm" then battack = battack.."humour disgorge "..snd.bashing.target elseif gmcp.Char.Vitals.aspect == "yellow" then battack = "humour befoul "..snd.bashing.target end end end else battack = "humour disgorge "..snd.bashing.target end end
function snd.carnifex_bash()
hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp)
if hp <=40 and snd.shield_check() then if snd.punisher then else battack = "touch shield" end elseif hp <= 60 and snd.soul >= 60 then battack = "soul consume for health" else battack = "pole spinslash "..snd.bashing.target end end
function snd.earthcaller_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp)
if hp <=40 and snd.shield_check() then if snd.punisher then else battack = "osso ribcage me" end elseif snd.toggles.lightning then battack = "tectonic ashfall "..snd.bashing.target else battack = "quash "..snd.bashing.target.." secret" end end
function snd.indorani_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp)
if hp <=40 and snd.shield_check() then if snd.punisher then else battack = "touch shield" end elseif hp <= 55 and hasSkill("Priestess") then battack = "fling priestess at me" else if snd.wielded.left.name =="bonedagger" or snd.wielded.right.name == "bonedagger" then battack = "flick bonedagger at "..snd.bashing.target else battack = "decay "..snd.bashing.target end end end
function snd.luminary_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) if hp <=40 and snd.shield_check() then if snd.punisher then else battack = "angel aura me" end elseif snd.toggles.lightning then battack = "evoke lightning "..snd.bashing.target else battack = "smite "..snd.bashing.target..snd.sep.."angel power" end end
function snd.monk_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) mp = 100*(gmcp.Char.Vitals.mp/gmcp.Char.Vitals.maxmp) --ensure battack is cleared going into this. battack = ""
if mp >= 30 and hp <= 75 and snd.transmutation and hasSkill("Transmute") then battack = battack.."transmute mana 1000"..snd.sep tempTimer(2,[[snd.transmutation = true]]) end
if not snd.balance.numbness and hasSkill("Numbness") then if hp <= 70 then battack = battack.."numb"..snd.sep end end
if (hp <=40 or snd.have_aff("blackout")) and snd.shield_check() then if snd.punisher then else battack = battack..snd.sep.."touch shield" end elseif snd.toggles.cobra and hasSkill("Cobra") then if snd.stance ~= "cobra" then battack = battack.."cbs" else battack = battack.."combo "..snd.bashing.target.." sdk sdk" end else battack = battack.."combo "..snd.bashing.target.." sdk ucp ucp" end end
function snd.oneiromancer_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp)
if hp <=40 and snd.shield_check() then if snd.punisher then else battack = "touch shield" end elseif hp <= 55 and hasSkill("Flower") then battack = "unfurl flower at me" elseif snd.toggles.newbie then battack = "starlight "..snd.bashing.target else if snd.wielded.left.name == "athame" or snd.wielded.right.name == "athame" then battack = "impel athame at "..snd.bashing.target else battack = "starlight "..snd.bashing.target end end end
function snd.praenomen_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) wp = 100*(gmcp.Char.Vitals.wp/gmcp.Char.Vitals.maxwp) bp = tonumber(gmcp.Char.Vitals.blood)
if hp <=40 and snd.shield_check() then if snd.punisher then else battack = "touch shield" end elseif hp <= 45 and bp >= 30 and snd.balance.mend and hasSkill("Mending") then battack = "mend" elseif bp >= 50 and hp <= 30 and hasSkill("Deluge") then if snd.balance.unbroken and snd.defenses.def_deluge.state == "down" then battack = "blood deluge"..snd.sep.."frenzy "..snd.bashing.target else battack = "frenzy "..snd.bashing.target end elseif bp <= 25 then battack = "feed corpse" else battack = "frenzy "..snd.bashing.target end end
function snd.ravager_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) mp = 100*(gmcp.Char.Vitals.mp/gmcp.Char.Vitals.maxmp) wp = 100*(gmcp.Char.Vitals.wp/gmcp.Char.Vitals.maxwp) battack = ""
if tonumber(gmcp.Char.Vitals.bleeding) > 1000 and snd.cauterize and mp >= 15 and hasSkill("Prolong") then battack = "enact prolong me"..snd.sep snd.cauterize = false tempTimer(2,[[snd.cauterize = true]]) end
if hp <=40 and snd.shield_check() then if snd.punisher then if hasSkill("Seethe") then battack = battack.."seethe" end else battack = battack.."touch shield" end elseif not snd.can_arms() and hasSkill("Concussion") then battack = battack.."concuss "..snd.bashing.target else battack = battack.."onslaught "..snd.bashing.target.." bully bully" end
end
function snd.revenant_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) mp = 100*(gmcp.Char.Vitals.mp/gmcp.Char.Vitals.maxmp) wp = 100*(gmcp.Char.Vitals.wp/gmcp.Char.Vitals.maxwp) endur = 100*(gmcp.Char.Vitals.ep/gmcp.Char.Vitals.maxep)
if hp <=40 and snd.shield_check() then if snd.punisher then else battack = "touch shield" end elseif snd.toggles.newbie then battack = "rivebash "..snd.bashing.target elseif (snd.wielded.left.name:find("warhammer") or snd.wielded.left.name:find("bastard") or snd.wielded.left.name:find("bardiche")) and hasSkill("Fell") then battack = "fell "..snd.bashing.target else battack = "dpl "..snd.bashing.target end if (hp >= 61 and wp >= 50 and hasSkill("Duskosa")) and not snd.toggles.newbie then battack = battack.. " duskosa duskosa" elseif not snd.toggles.newbie and hasSkill("Annul") then battack = "annul left"..snd.sep.."annul right"..snd.sep..battack end end
