if hp <=40 and snd.shield_check() then if snd.punisher then if snd.reflection_charge > 0 and snd.defenses.def_reflection.state ~= "deffed" then battack = "runecarve branch" end else battack = "touch shield" end elseif snd.defenses.def_reflection.state == "deffed" and hp <= 60 then -- let the reflection do its job elseif hp <= 60 and snd.reflection_charge > 0 then battack = "runecarve branch" elseif snd.toggles.newbie then battack = "hex enervate "..snd.bashing.target else if tonumber(gmcp.Char.Vitals.spiritpact) < 2 and hasSkill("Spiritpact") then battack = "spiritpact despair " ..snd.bashing.target..snd.sep.."hex malady "..snd.bashing.target else battack = "hex malady " ..snd.bashing.target end end end
