function snd.syssin_bash() local hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) local endur = 100*(gmcp.Char.Vitals.ep/gmcp.Char.Vitals.maxep) if hp <=40 and snd.shield_check() then if snd.punisher then else battack = "touch shield" end elseif snd.balance.backstab and hasSkill("Backstab") and endur > 75 then if snd.wielding("dirk") then battack = "backstab "..snd.bashing.target else if hasSkill("Quickwield") then battack = "quickwield left dirk"..snd.sep.."backstab "..snd.bashing.target else battack = "wield dirk" end end elseif snd.toggles.newbie then if snd.wielding("whip") then battack = "garrote "..snd.bashing.target else if hasSkill("Quickwield") then battack = "quickwield left whip"..snd.sep.."garrote "..snd.bashing.target else battack = "wield whip" end end else battack = "bite "..snd.bashing.target.." camus" end end
function snd.templar_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) mp = 100*(gmcp.Char.Vitals.mp/gmcp.Char.Vitals.maxmp) wp = 100*(gmcp.Char.Vitals.wp/gmcp.Char.Vitals.maxwp) endur = 100*(gmcp.Char.Vitals.ep/gmcp.Char.Vitals.maxep)
