function snd.zealot_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) mp = 100*(gmcp.Char.Vitals.mp/gmcp.Char.Vitals.maxmp) wp = 100*(gmcp.Char.Vitals.wp/gmcp.Char.Vitals.maxwp) battack = "" if tonumber(gmcp.Char.Vitals.bleeding) > 1000 and snd.cauterize and hasSkill("Cauterize") and mp >= 15 then battack = "enact cauterize me"..snd.sep snd.cauterize = false tempTimer(2,[[snd.cauterize = true]]) end
if hp <=40 then if snd.shield_check() and not snd.punisher then battack = battack.."touch shield" else if hasSkill("Respiration") then battack = battack.."respiration" end end elseif not snd.can_arms() and hasSkill("Sunkick") then battack = battack.."sunkick "..snd.bashing.target else battack = battack.."flow "..snd.bashing.target.." pummel pummel" end end
local ascendedAttacks = { malevolent = "adherent blossom", strife = "adherent shards", corruption = "adherent spiral", memory = "adherent shend", tyranny = "adherent oppress", instinct = "adherent embitter",
seraph = "seraph admonish", aetherial = "aetherial spiral", titan = "titan fissure", glacian = "glacian frostbite", nocturn = "nocturn curse", sagani = "sagani quarrel", chaos = "chaos havoc", tiarna = "tiarna fulminate", astral = "astral evert", none = "kill", }
function snd.none_bash() --super basic bashing function for 200's. hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp)
if hp <=40 and snd.shield_check() then if snd.punisher then else battack = "touch shield" end else battack = ascendedAttacks[snd.toggles.ascendedtype].." "..snd.target end end

function generic_bashing_name(mob)
local area = gmcp.Room.Info.area local target = "" if snd.generic_bashing_table_by_area[area] then target = snd.generic_bashing_table_by_area[area] elseif snd.generic_bashing_table_specific_names[mob] then target = snd.generic_bashing_table_specific_names[mob] else for k, v in ipairs(snd.generic_bashing_table_duplicate_names) do if string.match(' '.. mob:lower() ..' ', '%A'.. v ..'%A') ~= nil then target = v end end end
return target end
snd.generic_bashing_table_by_area = { ["the Forgotten Dome"] = "kelki", ["Xaanhal"] = "xorani", ["Luzith's Lair"] = "spider", ["the Bloodwood"] = "spirit", ["the Fractal Bloom"] = "entity", ["the Ayhesa Cliffs"] = "spellshaper", ["the Caverns of Mor"] = "undead", ["the Sparklight Rift"] = "elemental", ["the Centipede Cave"] = "centipede", ["the Teshen Caldera"] = "teshen", ["a snake pit"] = "snake", }
