function snd.do_rage() if snd.rage_check() and snd.can_rage() and snd.balance.rage then snd.send("rage") echo("(rage)") snd.last_type = "rage" snd.last_item = "rage" snd.waiting.balance = true tempTimer(snd.delay(), [[snd.waiting.balance = false]]) end end
function snd.rage_check()
if not snd.balance.rage then return false end
if not gmcp then return false end if hp ~= nil then if hp <= 1 then return false end end
local rage_affs = { "aff_hubris", "aff_pacifism", "aff_peace", "aff_agony", "aff_accursed", "aff_limp_veins", "aff_lovers_effect", "aff_laxity", "aff_superstition", "aff_generosity", "aff_justice", "aff_magnanimity",}
for _, v in pairs(rage_affs) do if snd.have_aff(v) then return true end end return false end

local mendingCures = { ["head"] = { "head_bruised_critical", "destroyed_throat", "crippled_throat", "head_bruised_moderate", "head_bruised", }, ["torso"] = { "torso_bruised_critical", "lightwound", "ablaze", "cracked_ribs", "torso_bruised_moderate", "torso_bruised", }, ["left arm"] = { "left_arm_bruised_critical", "left_arm_crippled", "a_crippled_arm", "left_arm_bruised_moderate", "left_arm_bruised", "left_arm_dislocated", }, ["right arm"] = { "right_arm_bruised_critical", "right_arm_crippled", "a_crippled_arm", "right_arm_bruised_moderate", "right_arm_bruised", "right_arm_dislocated", }, ["left leg"] = { "left_leg_bruised_critical", "left_leg_crippled", "a_crippled_leg", "left_leg_bruised_moderate", "left_leg_bruised", "left_leg_dislocated", }, ["right leg"] = { "right_leg_bruised_critical", "right_leg_crippled", "a_crippled_leg", "right_leg_bruised_moderate", "right_leg_bruised", "right_leg_dislocated", }, } local mendingMap = { legs = {"left leg", "right leg"}, arms = {"left arm", "right arm"}, skin = {"head", "torso", "left arm", "right arm", "left leg", "right leg"}, ["left leg"] = {"left leg"}, ["right leg"] = {"right leg"}, ["left arm"] = {"left arm"}, ["right arm"] = {"right arm"}, ["torso"] = {"torso"}, ["head"] = {"head"}, }
local function mending(limb) for _, aff in ipairs(mendingCures[limb]) do if aff:find("bruised") and ( (aff:find("critical") and snd.limb_bruising[limb] >= 3) or (not aff:find("critical") and snd.limb_bruising[limb] > 0) ) then snd.limb_bruising[limb] = math.min(3, snd.limb_bruising[limb]) - 1 table.insert(snd.last_affs, "bruising " .. limb) return true elseif snd.checkAff(aff) then if aff == "ablaze" then if not snd.checkAff("heatspear") then snd.target_cured("ablaze") snd.target_cured("crispy") table.insert(snd.last_affs, "ablaze") snd.flamestack = 0 return true end else snd.target_cured(aff) table.insert(snd.last_affs, aff) return true end end end return false end
function snd.mendingApply(location) if snd.checkAff("burnt_skin") then return end for _, limb in ipairs(mendingMap[location]) do if mending(limb) then return end end end

snd.all_class_attacks = { ["Enchantment"] = { Frostblast = function(target) snd.target_got("cold") end, }, --Luminary ["Spirituality"] = {
Crush = function(target, limb) snd.last_hit_limb = limb enableTrigger("Crush Confirmation") end, Crash = function(target, failure) if not failure then snd.target_got("disrupted") end end,
Smash = function(target, limb) snd.last_hit_limb = limb snd.last_limb_dmg = 25 snd.took_limb_dmg(snd.last_hit_limb, snd.last_limb_dmg) snd.onHit2({"Parry", "Dodge"}) end,
Aura = function(target) snd.used.shield = true if snd.hitting == "You" then snd.defenses.def_shield_tattoo.state = "deffed" end end,
Sear = function(target) snd.target_got("ablaze") snd.target_got("angel_seared") tempTimer(15, [[snd.target_cured("angel_seared")]]) end,
Strip = function(target, defense) snd.used.rebounding = false end,
Raze = function(target, defense) if defense == "shield" then snd.used.shield = false elseif defense == "rebounding" then snd.used.rebounding = false; snd.used.shield = false end end,
Chasten = function(target) if snd.battle_aff ~= "none" then snd.target_got(snd.battle_aff) table.insert(snd.last_affs, snd.battle_aff) end if snd.chasten_aff ~= "none" then snd.target_got(snd.chasten_aff) table.insert(snd.last_affs, snd.chasten_aff) end end,
Strike = function(target) table.insert(snd.maybe_affs, "paresis") snd.onHit2({"Dodge", "Rebounding"}) end, Punch = function(target) table.insert(snd.maybe_affs, "weariness") snd.onHit2({"Dodge", "Rebounding"}) end, Facesmash = function(target) if not snd.checksomeAffs({"misery", "sight"}, 1) then table.insert(snd.maybe_affs, 1, "misery") table.insert(snd.maybe_affs, 2, "sight") elseif snd.checkAff("misery") then table.insert(snd.maybe_affs, 1, "sight") else table.insert(snd.maybe_affs, 1, "misery") end snd.onHit2({"Dodge", "Rebounding"}, "Luminary Facesmash") end, Slam = function(target) if not snd.checksomeAffs({"asthma", "haemophilia"}, 1) then table.insert(snd.maybe_affs, 1, "asthma") table.insert(snd.maybe_affs, 2, "haemophilia") elseif snd.checkAff("asthma") then table.insert(snd.maybe_affs, 1, "haemophilia") else table.insert(snd.maybe_affs, 1, "asthma") end snd.onHit2({"Dodge", "Rebounding"}, "Luminary Slam") end, Brilliance = function(target) table.insert(snd.maybe_affs, "sight") snd.onHit2({"Dodge", "Rebounding"}) end,
},
["Devotion"] = { Dazzle = function(target) snd.target_got("confusion") snd.target_got("dizziness") end, Peace = function(target) snd.target_got("peace") end, Hellsight = function(target) snd.target_got("hellsight") end,
},
